mapper.select:
SELECT r.id id, r.name name, r.test_plan_id test_plan_id, r.start_time start_time, r.end_time end_time, r.status status, r.creator_id creator_id, r.sample_results sample_results, r.description description, r.create_time create_time, stp.test_plan_name test_plan_name, stp.project_name project_name, sa.username username FROM sugar_report r JOIN ( SELECT tap.test_plan_id test_plan_id, tap.test_plan_name test_plan_name, tap.project_id project_id, sp.name project_name FROM ( SELECT tp.id test_plan_id, name test_plan_name, ap.project_id FROM sugar_test_plan tp JOIN ( SELECT project_id project_id FROM sugar_account_project WHERE account_id = #{accountId} ) ap ON tp.project_id = ap.project_id ) tap JOIN sugar_project sp ON tap.project_id = sp.id
sp.name LIKE CANCAT('%', #{projectName}, '%')
) stp ON r.test_plan_id = stp.test_plan_id
stp.test_plan_name LIKE CONCAT('%', #{testPlanName}, '%')
JOIN sugar_account sa ON r.creator_id = sa.id
r.name LIKE CONCAT('%', #{name}, '%')
AND sa.username = #{creatorName}
AND DATE_FORMAT(r.create_time, '%Y-%m-%d') >= #{createStartTime}
AND DATE_FORMAT(r.create_time, '%Y-%m-%d') <= #{createEndTime}
